scriptionByCreatorAddress","outputs":[{"internalType":"address","name":"","type":"address"},{"internalType":"address","name":"","type":"address"},{"internalType":"uint256","name":"","type":"uint256"},{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"_creatorAddress","type":"address"},{"internalType":"address","name":"_followerAddress","type":"address"}],"name":"getSubscriptionContent","outputs":[{"internalType":"uint256[]","name":"","type":"uint256[]"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"usd","type":"uint256"}],"name":"getUSDPriceInCoins","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_description","type":"string"},{"internalType":"string","name":"_avatar","type":"string"},{"internalType":"uint256","name":"_subscriptionPrice","type":"uint256"}],"name":"registerAsCreator","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[],"name":"retrieveCreator","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[{"internalType":"uint256","name":"_contentID","type":"uint256"},{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_description","type":"string"},{"internalType":"uint256","name":"_contentType","type":"uint256"},{"internalType":"string","name":"_link","type":"string"}],"name":"updateContent","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_description","type":"string"},{"internalType":"string","name":"_avatar","type":"string"},{"internalType":"uint256","name":"_subscriptionPrice","type":"uint256"}],"name":"updateCreator","outputs":[],"stateMutability":"payable","type":"function"}],"de