pragma solidity ^0.8.9;

import "./Creators.sol";
import "./Subscriptions.sol";
import "./Signature.sol";

error Content__notOwner();
error Content__creatorNotFound();
error Content__subscriptionIsNotActive();
error Content__notFound();
error Content__notExist();
error Content__contentTypeNotExist();
error Content__notMainContract();

contract Content {


    struct ContentSt {
        string name;
        string description;
        uint256 contentType;
        string link;
        address creatorAddress;
    }

    ContentSt[] private content;

    Creators private creators_ref;
    Subscriptions private subscriptions_ref;

    address immutable owner;
    uint256 immutable version;
    address private mainContract;

    constructor(Creators _creatorsContract, Subscriptions _subscriptionsContract, uint256 _version) {
        creators_ref = _creatorsContract;
        subscriptions_ref = _subscriptionsContract;
        owner = msg.sender; 
        version = _version;
    }

    modifier onlyOwner() {
        // require(msg.sender == i_owner);
        if (msg.sender != owner) revert Content__notOwner();
        _;
    }

    modifier checkMainContract() {
        // require(msg.sender == i_owner);
        if (msg.sender != mainContract) revert Content__notMainContract();
        _;
    }

    function setMainContract(address _mainContractAddress) public payable onlyOwner{
        mainContract = _mainContractAddress;
    }

    function getContentLength() public view checkMainContract returns(uint256){
        return content.length;
    }

    function getSubscriptionContent(address _followerAddress, address _creatorAddress) public view returns(uint256[] memory) {
        if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Content__creatorNotFound();
        ( , , , address creatorAddress, , uint256[] memory contentIDs) = creators_ref.findCreatorDataByAdress(_creatorAddress);
        (, bool active) = subscriptions_ref.getSubscriptionInfo(_creatorAddress, _followerAddress); 
        if(active && creatorAddress ==_creatorAddress){
            return(contentIDs);
        }else{
            revert Content__subscriptionIsNotActive();
        }
    }

    function getContent(uint256 _contentID, bytes memory _signature, bytes32 _message) public view returns (string memory,
        string memory,
        uint256,
        string memory) {
        if (content.length == 0) revert Content__notExist();
        if (content.length <= _contentID) revert Content__notFound();
        address signer = Signature.getSignature(_message, _signature);
        address creatorAddress = content[_contentID].creatorAddress;
        bool active = false; 
        if(signer == creatorAddress){
            active=true;
        }else{
            (, active) = subscriptions_ref.getSubscriptionInfo(creatorAddress, signer); 
        }

        if(active){
                    ContentSt memory contentData = content[_contentID];
                    return(
                        contentData.name,
                        contentData.description,
                        contentData.contentType,
                        contentData.link
                    );
        }else{
            revert Content__subscriptionIsNotActive();
        }
    }

    function addContent(string memory _name, string memory _description, uint256 _contentType, string memory _link, address _creatorAddress) public payable checkMainContract{

       if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Content__creatorNotFound();

        if(_contentType != 0 && _contentType != 1){
            revert Content__contentTypeNotExist();
        }

        content.push(ContentSt(_name,
        _description,
        _contentType,
        _link,
        _creatorAddress
        ));

        uint256 contentID;
        contentID = content.length - 1;

        creators_ref.addContentToCreator(_creatorAddress, contentID);

    }

    function removeContent(uint256 _contentID, address _creatorAddress) public payable checkMainContract{

        if (content.length <= _contentID) revert Content__notFound();

        if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Content__creatorNotFound();

        if (content[_contentID].creatorAddress != _creatorAddress) revert Content__creatorNotFound();

        creators_ref.removeContentFromCreator(_creatorAddress, _contentID);

    }

    function updateContent(uint256 _contentID, string memory _name, string memory _description, uint256 _contentType, string memory _link, address _creatorAddress) public payable checkMainContract{

        if (content.length <= _contentID) revert Content__notFound();

        if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Content__creatorNotFound();

        if (content[_contentID].creatorAddress != _creatorAddress) revert Content__creatorNotFound();

        content[_contentID] = ContentSt(_name,
        _description,
        _contentType,
        _link,
        _creatorAddress
        );

    }





}","keccak256":"0x43c4c101fe5a6e846ae2e979dafd2d4fbf8c6883aa4c13b0a7ada5c70b5b75b5","license":"Unlicense"},"contracts/Creators.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "hardhat/console.sol";

error Creators__creatorAlreadyExists();
error Creators__noCreators();
error Creators__creatoreNotFound();
error Creators__notMainContract();
error Creators__notOwner();
error Creators__contentIDalreadyConnected();
error Creators__contentIDnotFound();
error Creators__creatorsEnded();

contract Creators {

struct CreatorsStruct {
        string name;
        string description;
        string avatar;
        address creatorAddress;
        uint256 subscriptionPrice;
        uint256[] contentIDs;
        bool visible;
    }

CreatorsStruct[] private creators;


mapping(address  => uint256) private creatorID;
mapping(uint256  => address) private contentToCreatorAddress;


address private mainContract; 
address private contentContract; 
address immutable owner;
uint256 immutable version;

constructor(uint256 _version){
    owner = msg.sender;
    version = _version;
}


    modifier checkMainContract() {
        // require(msg.sender == i_owner);
        if (msg.sender != mainContract) revert Creators__notMainContract();
        _;
    }

    modifier checkMainOrCreatorContract() {
        // require(msg.sender == i_owner);
        if(msg.sender == mainContract || msg.sender == contentContract){

        }else{
            revert Creators__notMainContract();
        }
        _;
    }

    modifier onlyOwner() {
        // require(msg.sender == i_owner);
        if (msg.sender != owner) revert Creators__notOwner();
        _;
    }

    function setMainContract(address _mainContractAddress) public payable onlyOwner{
        mainContract = _mainContractAddress;
    }
    function setContentContract(address _contentContract) public payable onlyOwner{
        contentContract = _contentContract;
    }

    function checkCreatorExistance(address _creatorAddress) public view returns(bool){
            uint256 creatorid = creatorID[_creatorAddress];
            if(creators.length > 0){
                if(creatorid == 0){
                    if(_creatorAddress == creators[creatorid].creatorAddress){
                        return true;
                    }else{
                        return false;
                    }
                }else{
                    return true;
                }
            }else{
                  return false;
            }

    }


    function registerAsCreator(string memory _name, string memory _description, string memory _avatar, uint256 _subscriptionPrice, address _creatorAddress) public payable checkMainContract {
            if(creators.length > 0){
                if(creators[creatorID[_creatorAddress]].creatorAddress == _creatorAddress){
                    revert Creators__creatorAlreadyExists();
                }
            }
            uint256[] memory contentIDs;
                creatorID[_creatorAddress] = creators.length;
                creators.push(CreatorsStruct(_name,
                _description,
                _avatar,
                _creatorAddress,
                _subscriptionPrice,
                contentIDs,
                true
                ));
    }

    function updateCreator(string memory _name, string memory _description, string memory _avatar, uint256 _subscriptionPrice, address _creatorAddress) public payable checkMainContract {
            if(checkCreatorExistance(_creatorAddress)){

            uint256 creatorid = creatorID[_creatorAddress];
            uint256[] memory contentIDs = creators[creatorid].contentIDs;
            bool visible = creators[creatorid].visible;

            creators[creatorid] = CreatorsStruct(
                _name,
                _description,
                _avatar,
                _creatorAddress,
                _subscriptionPrice,
                contentIDs,
                visible
            );

            }else{
                revert Creators__creatoreNotFound();
            }

    }

    function changeCreatorVisibility(bool _visible, address _creatorAddress) public payable checkMainContract {
            if(checkCreatorExistance(_creatorAddress)){
                uint256 creatorid = creatorID[_creatorAddress];
                creators[creatorid].visible = _visible;
            }else{
                revert Creators__creatoreNotFound();
            }

    }

    function findCreatorIDByAdress(address _creatorAddress) public view returns (uint256){
        if(checkCreatorExistance(_creatorAddress)){

        uint256 creatorid = creatorID[_creatorAddress];
        return creatorid;
          }else{
            revert Creators__creatoreNotFound();
        }


    }

    function findCreatorDataByAdress(address _creatorAddress) public view returns 
        (string memory,
        string memory,
        string memory,
        address,
        uint256,
        uint256[] memory){
        if(checkCreatorExistance(_creatorAddress)){
        uint256 creatorid = creatorID[_creatorAddress];
        if(creators[creatorid].visible){
            return (creators[creatorid].name, 
                    creators[creatorid].description, 
                    creators[creatorid].avatar, 
                    creators[creatorid].creatorAddress, 
                    creators[creatorid].subscriptionPrice, 
                    creators[creatorid].contentIDs);
            }
        }
        revert Creators__creatoreNotFound();
    }


    function getCreatorsIDs(uint256 _startIndex, uint256 _limit) public view returns 
        (address[] memory, uint256, uint256){
        if(_startIndex >= creators.length) revert Creators__creatorsEnded();
        uint256 count = creators.length - _startIndex;
        if(_limit > count && count > 20){
            _limit = 20;
        }else if(_limit > count && count < 20){
            _limit = count;
        }else if (_limit > 20){
             _limit = 20;
        } 
        address[] memory _indexes  = new address[](_limit);
        uint256 n = 0;
        uint256 nextIndex;
        for(uint256 i=_startIndex; i<(_startIndex+_limit); i++){
            if(i<creators.length){
                if(creators[i].visible && n<_indexes.length){
                    _indexes[n] = creators[i].creatorAddress;
                    n ++;
                }else if(n==_indexes.length){
                    break;
                }else{
                    _limit++;
                }
            }
            if(i == _startIndex+_limit-1){
                nextIndex = i+1;
                if(i+1 >= creators.length){
                    count = 0;
                }else{
                    count = creators.length-i-1;
                }
            }
        }

        return(_indexes, count, nextIndex);

    }

    function addContentToCreator (address _creatorAddress, uint256 _contentID) public payable checkMainOrCreatorContract {
        if(checkCreatorExistance(_creatorAddress)){
            if(contentToCreatorAddress[_contentID] == 0x0000000000000000000000000000000000000000){
            uint256 creatorid = creatorID[_creatorAddress];
            creators[creatorid].contentIDs.push(_contentID);
            contentToCreatorAddress[_contentID] = _creatorAddress;
            }else{
                revert Creators__contentIDalreadyConnected();
            }
        }else{
            revert Creators__creatoreNotFound();
        }

    }

    function removeContentFromCreator (address _creatorAddress, uint256 _contentID) public payable checkMainOrCreatorContract {
        if(checkCreatorExistance(_creatorAddress)){
            if(contentToCreatorAddress[_contentID] != 0x0000000000000000000000000000000000000000){
                uint256 creatorid = creatorID[_creatorAddress];
                uint256[] memory contentIDs = creators[creatorid].contentIDs;
                uint256 contentIDinArray=contentIDs.length;
                for(uint256 i=0; i<contentIDs.length; i++){
                    if(contentIDs[i] == _contentID){
                        contentIDinArray = i; 
                    }
                    if (i >= contentIDinArray && i != (contentIDs.length-1)){
                        contentIDs[i] = contentIDs[i+1];
                    }
                }
                creators[creatorid].contentIDs = contentIDs;
                creators[creatorid].contentIDs.pop();

            }else{
                revert Creators__contentIDnotFound();
            }
        }else{
            revert Creators__creatoreNotFound();
        }

    }


}","keccak256":"0x5c3eb986203bfe09c1e04d924448cef7d666aa74bd1eec924da1a4b19279b6f7","license":"MIT"},"contracts/Main.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "hardhat/console.sol";
import "./PriceConverter.sol";
import "./Creators.sol";
import "./Subscriptions.sol";
import "./Content.sol";
import "./Signature.sol";

error Main__NotOwner();
error Main__creatorNotFound();
error Main__NotEnoughtRights();

contract Main {

uint256 private balance; 
uint256 public immutable contractFee;
address private immutable owner;
uint256 immutable version;

AggregatorV3Interface private s_priceFeed;
Creators private creators_ref;
Subscriptions private subscriptions_ref;
Content private content_ref;

mapping(address  => uint256) private creatorBalance;


event creatorRegistration(address indexed creatorAddress);
event creatorUpdate(string _name, string _description, string _avatar, uint256 _subscriptionPrice, address indexed creatorAddress);
event creatorDetetion(address indexed creatorAddress);
event creatorRetrieve(address indexed creatorAddress);
event contentAdded(uint256 indexed contentID);
event contentDeleted();
event contentUpdated(uint256 indexed contentID);
event subscriptionBought(address indexed followerAddress, address indexed creatorAddress);
event contractWithdrawEvent();
event creatorWithdrawEvent(address indexed creatorAddress);

constructor(address _priceFeed, Creators _creatorsContract, Subscriptions _subscriptionsContract, Content _contentContract, uint256 _version) {
        creators_ref = _creatorsContract;
        s_priceFeed = AggregatorV3Interface(_priceFeed);
        subscriptions_ref = _subscriptionsContract;
        content_ref = _contentContract;
        owner = msg.sender;
        contractFee = 20;
        balance = 0;
        version = _version;
    }

modifier onlyOwner() {
        // require(msg.sender == i_owner);
        if (msg.sender != owner) revert Main__NotOwner();
        _;
    }

function registerAsCreator(string memory _name, string memory _description, string memory _avatar, uint256 _subscriptionPrice) public payable {
            creators_ref.registerAsCreator(_name, _description, _avatar, _subscriptionPrice, msg.sender);
            creatorBalance[msg.sender] = 0;
            emit creatorRegistration(msg.sender);

    }

function updateCreator(string memory _name, string memory _description, string memory _avatar, uint256 _subscriptionPrice) public payable {
            if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
            creators_ref.updateCreator(_name, _description, _avatar, _subscriptionPrice, msg.sender);
            emit creatorUpdate(_name, _description, _avatar, _subscriptionPrice, msg.sender);
    }

function deleteCreator() public payable {
            if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
            creators_ref.changeCreatorVisibility(false, msg.sender);  
            emit creatorDetetion(msg.sender);
    }

function retrieveCreator() public payable {
            if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
            creators_ref.changeCreatorVisibility(true, msg.sender);  
            emit creatorRetrieve(msg.sender);
    }

function findCreatorDataByAdress(address _creatorAddress) public view returns 
        (string memory,
        string memory,
        string memory,
        address,
        uint256,
        uint256[] memory){
        if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Main__creatorNotFound();
        (string memory name, string memory description, string memory avatar, address creatorAddress, uint256 subscriptionPrice, uint256[] memory contentIDs) = creators_ref.findCreatorDataByAdress(_creatorAddress);
        return(name, 
                description, 
                avatar, 
                creatorAddress, 
                subscriptionPrice, 
                contentIDs);
    }

function getCreatorsIDs(uint256 _startIndex, uint256 _limit) public view returns 
        (address[] memory, uint256, uint256){
        (address[] memory indexes, uint256 count, uint256 nextIndex) = creators_ref.getCreatorsIDs(_startIndex, _limit);
        return(indexes, count, nextIndex);

    }


function addContent(string memory _name, string memory _description, uint256 _contentType, string memory _link) public payable {
        if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
        content_ref.addContent(_name, _description, _contentType, _link, msg.sender);
        uint256 contentID = content_ref.getContentLength();
        contentID --;
        emit contentAdded(contentID);
    }

function deleteContent(uint256 _contentID) public payable {
        if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
        content_ref.removeContent(_contentID, msg.sender);
        emit contentDeleted();
    }

function updateContent(uint256 _contentID, string memory _name, string memory _description, uint256 _contentType, string memory _link) public payable {
        if (!creators_ref.checkCreatorExistance(msg.sender)) revert Main__creatorNotFound();
        content_ref.updateContent(_contentID, _name, _description, _contentType, _link, msg.sender);
        emit contentUpdated(_contentID);
    }

function buySubscription(address _creatorAddress) public payable{
    if (!creators_ref.checkCreatorExistance(_creatorAddress)) revert Main__creatorNotFound();
    subscriptions_ref.buySubscription( _creatorAddress,  msg.sender,  msg.value);
    uint256 balanceIncrease = msg.value / contractFee;
    uint256 creatorBalanceIncrease = msg.value - balanceIncrease;
    creatorBalance[_creatorAddress] += creatorBalanceIncrease;
    balance += balanceIncrease;
    emit subscriptionBought(msg.sender, _creatorAddress);
}

function getFollowerSubscriptions(address _follower) public view returns(address[] memory){
    address[] memory _followerSubscriptions = subscriptions_ref.getFollowerSubscriptions(_follower); 
    return _followerSubscriptions;

}


function getSubscriptionByCreatorAddress(address _creator, address _follower) public view  returns(address, address, uint, bool){
        (uint256 subscriptionEnd, bool active) = subscriptions_ref.getSubscriptionInfo(_creator, _follower);

                return (_creator, 
                    _follower, 
                    subscriptionEnd, 
                    active);

    }

    function getContent(uint256 _contentID, bytes memory _signature, bytes32 _message) public view returns (string memory,
        string memory,
        uint256,
        string memory) {

        (string memory name, string memory description, uint256 contentType, string memory link) = content_ref.getContent(_contentID, _signature, _message);
        return(
                        name,
                        description,
                        contentType,
                        link
                    );


    }

    function getSubscriptionContent(address _creatorAddress, address _followerAddress) public view returns(uint256[] memory) {
       uint256[] memory contentIDs = content_ref.getSubscriptionContent( _followerAddress, _creatorAddress);
        return(contentIDs);

    }

    function getContractBalance(bytes memory _signature, bytes32 _message) public view returns(uint256){

        require(_signature.length == 65, "invalid signature length");
            bytes32 r;
            bytes32 s;
            uint8 v;
            assembly {
            r := mload(add(_signature, 32))
            s := mload(add(_signature, 64))
            v := byte(0, mload(add(_signature, 96)))
            }
            address signer = ecrecover(_message, v, r, s);
        if(signer == owner){
            return balance;
        }else{
            revert Main__NotOwner();
        }

    }

    function getCreatorBalance(bytes memory _signature, bytes32 _message) public view returns(uint256){

        address signer = Signature.getSignature(_message, _signature);
        ( , , , address creatorAddress, ,) = creators_ref.findCreatorDataByAdress(signer);
        if(creatorAddress == signer){
            return creatorBalance[signer];
        }else{
            revert Main__NotEnoughtRights();
        }

    }

    function contractWithdraw() public payable onlyOwner{

        (bool success, ) = owner.call{value: balance}("");
        require(success);
        emit contractWithdrawEvent();
    }

    function creatorWithdraw() public payable {

        address creatorAddress = msg.sender;
        (bool success, ) = creatorAddress.call{value: creatorBalance[creatorAddress]}("");
        require(success);
        creatorBalance[creatorAddress] = 0;
        emit creatorWithdrawEvent(msg.sender);

    }

    function getPriceLevel() public view returns (uint256){

        return PriceConverter.getPrice(s_priceFeed);
    }

    function getUSDPriceInCoins(uint256 usd) public view returns (uint256){

        return PriceConverter.getUSDPriceInCoins(s_priceFeed, usd);
    }

}
